import {useEffect, useState} from "react";
import {detailQuestions} from "../service/QuestionService";
import {useNavigate, useParams} from "react-router-dom";

const QuestionsDetailComponent = ()=>{
    const [questionsDetail,setQuestionsDetail] = useState({});
    const {id}=useParams();
    const navigate = useNavigate();
    useEffect(() => {
        const fetchData =async ()=>{
            const data = await detailQuestions(id)
            setQuestionsDetail(data);
        }
        fetchData();
    }, [questionsDetail]);

    return(
        <>
            {questionsDetail&&questionsDetail.map((questionsDetail)=>(
                <>
                    <p>{questionsDetail.id}</p>
                    <p>{questionsDetail.questionsContent}</p>
                    <p>{questionsDetail.answersContent}</p>
                    <p>{questionsDetail.correct}</p>
                </>


            ))}
        </>
    );
}
export default QuestionsDetailComponent;
