import {useEffect, useState} from "react";
import {getAllQuestions} from "../service/QuestionService";

const QuestionService = () => {
    const [questions, setQuestions] = useState([]);
    const [page, setPage] = useState(0);
    const [categorySearch,setCategorySearch] = useState('1')
    const [totalPage, setTotalPage] = useState(0);

    useEffect(() => {
        const fetchData = async () => {
            const {data,totalPage} = await getAllQuestions(page,categorySearch);
            setQuestions(data);
            setTotalPage(totalPage);
        }
        fetchData()
    }, [page,categorySearch]);
    const handleNextPage=()=>{
        if (page<totalPage){
            setPage((preV)=>preV+1)
        }

    }
    const handlePrePage=()=>{
        if (page>0){
            setPage((preV)=>preV-1)
        }

    }

    return (
        <>
            <div className="container mt-5">
                <h2 className="mb-4">Danh Sách Câu Hỏi</h2>

                <table className="table table-bordered mt-4">
                    <thead>
                    <tr>
                        <th>ID</th>
                        <th>content</th>
                        <th>category</th>
                    </tr>
                    </thead>
                    <tbody>
                    {questions && questions.map(q => (
                        <tr key={q.id}>
                            <td>{q.id}</td>
                            <td>{q.content}</td>
                            <td>{q.category.name}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                <div style={{justifyContent: 'center', textAlign: 'center'}}>
                    <button className={'btn btn-sm btn-outline-success btn-hover'}
                            onClick={() => (handlePrePage())}>Trang
                        Trước
                    </button>
                    {[...new Array(totalPage)].map((p, i) => (
                        <button className={`btn btn-sm btn-outline-success btn-hover ${page === i ? 'active' : ''}`}
                                onClick={() => (setPage(i))}>{i + 1}</button>
                    ))}
                    <button className={'btn btn-sm btn-outline-success btn-hover'}
                            onClick={() => (handleNextPage())}>Trang
                        Sau
                    </button>
                </div>
            </div>

        </>
    );
}
export default QuestionService;
